import React, { useContext } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Navbar, Nav, Jumbotron } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';

import PAGES from '../data/PAGES';

import { AuthContext } from '../contexts';

function HeaderBar() {
    const { authInfo } = useContext(AuthContext);
    const navItems = PAGES.map((item, index) =>
        <Nav.Link to={item.href}
            as={NavLink}
            key={index} >
            {item.label}
        </Nav.Link>
    );
    const homeButton = <Nav.Link to='/' key='homeButton' as={NavLink}>
        <FontAwesomeIcon icon="home" color="white" size="lg" />
    </Nav.Link>;
    navItems.unshift(homeButton)
    if (authInfo.authenticated) {
        return (
            <Navbar bg="primary" style={{ backgroundColor: 'rgb(0, 50, 100)', color: 'white' }}>
                <Navbar.Collapse>

                    {navItems}
                </Navbar.Collapse>
                <span style={{ color: 'white' }}>Welcome {authInfo.user} !</span>
            </Navbar>
        );
    } else {
        return (
            <Jumbotron style={{ backgroundColor: 'rgb(0, 50, 100)', color: 'white' }}>
                <h1>SAS Viya Demo Application</h1>
            </Jumbotron>
        );
    }
}

export default HeaderBar;
